import React from 'react';
import {DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
function Column(prop) {
    return (
        <div {...prop.provided.droppableProps}
        ref={prop.provided.innerRef}
        style={{background: prop.snapshot.isDraggingOver ? 'lightblue' : 'lightgrey', padding: 4, width: 250, minHeight: 500}}
        >
            {prop.column.items.map((item, index) => {
                                            return (
                                                <Draggable key={item.id} draggableId={item.id} index={index}>
                                                    {(provided, snapshot) => {
                                                        return (
                                                            <Task provided={provided} snapshot= {snapshot} text = {item.content} />


                                                        );
                                                    }}
                                                </Draggable>
                                            );
                                        })}
                                    {provided.placeholder}
        </div>
    )
}
export default Column;
